// Create bar chart HTML
function createBarChart(labels, values, title) {
    const maxValue = Math.max(...values, 1);
    const bars = labels.map((label, index) => {
        const percentage = (values[index] / maxValue) * 100;
        return `
            <div style="margin-bottom: 12px;">
                <div style="display: flex; justify-content: space-between; margin-bottom: 6px;">
                    <span style="font-size: 13px; color: var(--text-color); font-weight: 500;">${label}</span>
                    <span style="font-size: 13px; color: var(--text-light); font-weight: 600;">${values[index]}</span>
                </div>
                <div style="width: 100%; height: 8px; background: var(--border-light); border-radius: 4px; overflow: hidden;">
                    <div style="width: ${percentage}%; height: 100%; background: linear-gradient(90deg, var(--primary-color) 0%, var(--primary-light) 100%); transition: width 0.5s ease;"></div>
                </div>
            </div>
        `;
    }).join('');

    return `<div style="background: var(--light-color); padding: 20px; border-radius: 12px; border: 1px solid var(--border-color);">${bars}</div>`;
}

// Create pie chart HTML (simple version)
function createPieChart(labels, values, colors) {
    const total = values.reduce((sum, val) => sum + val, 0);
    if (total === 0) {
        return '<p style="color: var(--text-light); text-align: center; padding: 20px;">No data available</p>';
    }

    const items = labels.map((label, index) => {
        const percentage = (values[index] / total) * 100;
        return `
            <div style="display: flex; align-items: center; justify-content: space-between; padding: 12px; background: var(--white); border-radius: 8px; margin-bottom: 8px; border-left: 4px solid ${colors[index]}">
                <div style="display: flex; align-items: center; gap: 12px;">
                    <div style="width: 12px; height: 12px; border-radius: 50%; background: ${colors[index]};"></div>
                    <span style="font-size: 14px; color: var(--text-color); font-weight: 500;">${label}</span>
                </div>
                <div style="display: flex; align-items: center; gap: 12px;">
                    <span style="font-size: 16px; font-weight: 700; color: var(--text-color);">${values[index]}</span>
                    <span style="font-size: 13px; color: var(--text-light);">${Math.round(percentage)}%</span>
                </div>
            </div>
        `;
    }).join('');

    return `<div style="background: var(--light-color); padding: 20px; border-radius: 12px; border: 1px solid var(--border-color);">${items}</div>`;
}

// Load recent activities
async function loadRecentActivities(forceRefresh = false) {
    if (!window.db || !window.userEmail) return;

    const activitiesEl = document.getElementById('recent-activities');
    if (!activitiesEl) return;

    // Check cache first
    if (!forceRefresh && isCacheValid('activities')) {
        console.log('[loadRecentActivities] Using cached data - instant load');
        renderCachedActivitiesData();
        return;
    }

    try {
        // Show loading state
        activitiesEl.innerHTML = '<div style="text-align: center; padding: 20px; color: var(--text-light);"><p>Loading activities...</p></div>';

        const {
            collection,
            query,
            where,
            orderBy,
            limit,
            getDocs
        } = await import('https://www.gstatic.com/firebasejs/10.7.1/firebase-firestore.js');

        const activities = [];
        const maxActivities = 3;

        // For island users, query by island; for campaign managers, query by email/campaignEmail
        const isIslandUser = window.isIslandUser && window.islandUserData && window.islandUserData.island;
        const island = isIslandUser ? window.islandUserData.island : null;

        // Get recent candidates
        try {
            const candidatesQuery = query(
                collection(window.db, 'candidates'),
                isIslandUser ? where('island', '==', island) : where('email', '==', window.userEmail),
                limit(5)
            );
            const candidatesSnap = await getDocs(candidatesQuery);
            candidatesSnap.forEach(doc => {
                const data = doc.data();
                // Only include if email/island matches (security check)
                const emailMatches = data.email === window.userEmail || data.campaignEmail === window.userEmail;
                const islandMatches = isIslandUser && data.island === island;
                if (emailMatches || islandMatches) {
                    activities.push({
                        type: 'candidate',
                        icon: '',
                        text: `New candidate added: ${data.name || 'Unknown'}`,
                        timestamp: data.createdAt || data.updatedAt || null,
                        id: doc.id
                    });
                }
            });
        } catch (err) {
            console.warn('Error loading candidate activities:', err);
        }

        // Get recent voters
        try {
            const votersQuery = query(
                collection(window.db, 'voters'),
                isIslandUser ? where('island', '==', island) : where('email', '==', window.userEmail),
                limit(5)
            );
            const votersSnap = await getDocs(votersQuery);
            votersSnap.forEach(doc => {
                const data = doc.data();
                // Only include if email/island matches (security check)
                const emailMatches = data.email === window.userEmail || data.campaignEmail === window.userEmail;
                const islandMatches = isIslandUser && data.island === island;
                if (emailMatches || islandMatches) {
                    activities.push({
                        type: 'voter',
                        icon: '',
                        text: `New voter registered: ${data.name || 'Unknown'}`,
                        timestamp: data.createdAt || data.updatedAt || null,
                        id: doc.id
                    });
                }
            });
        } catch (err) {
            console.warn('Error loading voter activities:', err);
        }

        // Get recent events
        try {
            const eventsQuery = query(
                collection(window.db, 'events'),
                isIslandUser ? where('island', '==', island) : where('campaignEmail', '==', window.userEmail),
                limit(5)
            );
            const eventsSnap = await getDocs(eventsQuery);
            eventsSnap.forEach(doc => {
                const data = doc.data();
                // Only include if email/island matches (security check)
                const emailMatches = data.email === window.userEmail || data.campaignEmail === window.userEmail;
                const islandMatches = isIslandUser && data.island === island;
                if (emailMatches || islandMatches) {
                    activities.push({
                        type: 'event',
                        icon: '',
                        text: `Event scheduled: ${data.eventName || 'Unknown'}`,
                        timestamp: data.eventDate || data.createdAt || null,
                        id: doc.id
                    });
                }
            });
        } catch (err) {
            console.warn('Error loading event activities:', err);
        }

        // Get recent calls
        try {
            const callsQuery = query(
                collection(window.db, 'calls'),
                isIslandUser ? where('island', '==', island) : where('campaignEmail', '==', window.userEmail),
                limit(5)
            );
            const callsSnap = await getDocs(callsQuery);
            callsSnap.forEach(doc => {
                const data = doc.data();
                // Only include if email/island matches (security check)
                const emailMatches = data.email === window.userEmail || data.campaignEmail === window.userEmail;
                const islandMatches = isIslandUser && data.island === island;
                if (emailMatches || islandMatches) {
                    activities.push({
                        type: 'call',
                        icon: '',
                        text: `Call made to: ${data.voterName || 'Unknown'}`,
                        timestamp: data.callDate || data.createdAt || null,
                        id: doc.id
                    });
                }
            });
        } catch (err) {
            console.warn('Error loading call activities:', err);
        }

        // Get recent pledges
        try {
            const pledgesQuery = query(
                collection(window.db, 'pledges'),
                isIslandUser ? where('island', '==', island) : where('email', '==', window.userEmail),
                limit(5)
            );
            const pledgesSnap = await getDocs(pledgesQuery);
            pledgesSnap.forEach(doc => {
                const data = doc.data();
                // Only include if email/island matches (security check)
                const emailMatches = data.email === window.userEmail || data.campaignEmail === window.userEmail;
                const islandMatches = isIslandUser && data.island === island;
                if (emailMatches || islandMatches) {
                    activities.push({
                        type: 'pledge',
                        icon: '',
                        text: `Pledge received from: ${data.voterName || 'Unknown'}`,
                        timestamp: data.pledgeDate || data.createdAt || null,
                        id: doc.id
                    });
                }
            });
        } catch (err) {
            console.warn('Error loading pledge activities:', err);
        }

        // Sort activities by timestamp (most recent first)
        activities.sort((a, b) => {
            const timeA = a.timestamp ? (a.timestamp.toDate ? a.timestamp.toDate().getTime() : new Date(a.timestamp).getTime()) : 0;
            const timeB = b.timestamp ? (b.timestamp.toDate ? b.timestamp.toDate().getTime() : new Date(b.timestamp).getTime()) : 0;
            return timeB - timeA;
        });

        // Take only the most recent activities
        const recentActivities = activities.slice(0, maxActivities);

        // Store in cache
        dataCache.activities.data = recentActivities;
        dataCache.activities.timestamp = Date.now();
        dataCache.activities.userEmail = window.userEmail;

        // Render activities
        if (recentActivities.length === 0) {
            activitiesEl.innerHTML = '<div class="activity-item" style="text-align: center; padding: 20px; color: var(--text-light);"><p>No recent activities</p></div>';
        } else {
            activitiesEl.innerHTML = recentActivities.map(activity => {
                let timeText = 'Recently';
                if (activity.timestamp) {
                    try {
                        const timestamp = activity.timestamp.toDate ? activity.timestamp.toDate() : new Date(activity.timestamp);
                        const now = new Date();
                        const diffMs = now - timestamp;
                        const diffMins = Math.floor(diffMs / 60000);
                        const diffHours = Math.floor(diffMs / 3600000);
                        const diffDays = Math.floor(diffMs / 86400000);

                        if (diffMins < 1) {
                            timeText = 'Just now';
                        } else if (diffMins < 60) {
                            timeText = `${diffMins} minute${diffMins !== 1 ? 's' : ''} ago`;
                        } else if (diffHours < 24) {
                            timeText = `${diffHours} hour${diffHours !== 1 ? 's' : ''} ago`;
                        } else if (diffDays < 7) {
                            timeText = `${diffDays} day${diffDays !== 1 ? 's' : ''} ago`;
                        } else {
                            timeText = timestamp.toLocaleDateString();
                        }
                    } catch (err) {
                        console.warn('Error formatting timestamp:', err);
                    }
                }

                return `
                    <div class="activity-item">
                        <div class="activity-icon">${activity.icon}</div>
                        <div class="activity-content">
                            <p class="activity-text">${activity.text}</p>
                            <span class="activity-time">${timeText}</span>
                        </div>
                    </div>
                `;
            }).join('');
        }
    } catch (error) {
        console.error('Error loading recent activities:', error);
        activitiesEl.innerHTML = '<div class="activity-item" style="text-align: center; padding: 20px; color: var(--text-light);"><p>Error loading activities</p></div>';
    }
}

