// Update sort direction indicators in candidate table header
function updateCandidatesSortIndicators() {
    const state = paginationState.candidates;
    const wrapper = document.getElementById('candidates-table-detail-wrapper');
    if (!wrapper) return;
    wrapper.querySelectorAll('.sortable-th').forEach(th => {
        const key = th.getAttribute('data-sort');
        const ind = th.querySelector('.sort-indicator');
        if (!ind) return;
        if (state.sortBy === key) {
            ind.textContent = state.sortDir === 1 ? ' ' : ' ';
            ind.style.visibility = 'visible';
        } else {
            ind.textContent = '';
            ind.style.visibility = 'hidden';
        }
    });
}

// Sort candidates array by current sort state (does not mutate original)
function sortCandidatesArray(candidatesArray) {
    const state = paginationState.candidates;
    if (!state.sortBy) return [...candidatesArray];
    const key = state.sortBy;
    const dir = state.sortDir;
    const sorted = [...candidatesArray];
    sorted.sort((a, b) => {
        const rawA = a[key];
        const rawB = b[key];
        const va = (rawA != null && rawA !== '') ? String(rawA).trim().toLowerCase() : '';
        const vb = (rawB != null && rawB !== '') ? String(rawB).trim().toLowerCase() : '';
        let cmp = 0;
        if (va !== vb) cmp = va < vb ? -1 : 1;
        if (cmp !== 0) return dir * cmp;
        // Stable sort: tie-break by name then id
        const na = (a.name != null) ? String(a.name).toLowerCase() : '';
        const nb = (b.name != null) ? String(b.name).toLowerCase() : '';
        if (na !== nb) return na < nb ? -1 : 1;
        return (a.id || '').localeCompare(b.id || '');
    });
    return sorted;
}

// Load candidates data
async function loadCandidatesData(forceRefresh = false) {
    if (!window.db || !window.userEmail) return;

    const tbody = document.getElementById('candidates-table-body');
    if (!tbody) return;

    // Check cache first
    if (!forceRefresh && isCacheValid('candidates')) {
        console.log('[loadCandidatesData] Using cached data - instant load');
        renderCachedCandidatesData();
        return;
    }

    // Show skeleton loading
    showTableSkeleton(tbody, 5, 5);

    try {
        const {
            collection,
            query,
            where,
            getDocs
        } = await import('https://www.gstatic.com/firebasejs/10.7.1/firebase-firestore.js');

        let snapshot;
        // For island users, query by island field; for campaign managers, query by email/campaignEmail
        if (window.isIslandUser && window.islandUserData && window.islandUserData.island) {
            console.log('[loadCandidatesData] Island user - querying by island:', window.islandUserData.island);
            try {
                const candidatesQuery = query(collection(window.db, 'candidates'), where('island', '==', window.islandUserData.island));
                snapshot = await getDocs(candidatesQuery);
            } catch (islandQueryError) {
                console.error('[loadCandidatesData] Query by island failed:', islandQueryError);
                snapshot = {
                    docs: []
                };
            }
        } else {
            // Try both email and campaignEmail fields for campaign managers
            const emailDocs = [];
            const campaignEmailDocs = [];
            const allDocIds = new Set();

            try {
                const emailQuery = query(collection(window.db, 'candidates'), where('email', '==', window.userEmail));
                const emailSnapshot = await getDocs(emailQuery);
                emailSnapshot.docs.forEach(doc => {
                    if (!allDocIds.has(doc.id)) {
                        emailDocs.push(doc);
                        allDocIds.add(doc.id);
                    }
                });
            } catch (e) {
                console.warn('[loadCandidatesData] Query by email failed:', e);
            }

            try {
                const campaignEmailQuery = query(collection(window.db, 'candidates'), where('campaignEmail', '==', window.userEmail));
                const campaignSnapshot = await getDocs(campaignEmailQuery);
                campaignSnapshot.docs.forEach(doc => {
                    if (!allDocIds.has(doc.id)) {
                        campaignEmailDocs.push(doc);
                        allDocIds.add(doc.id);
                    }
                });
            } catch (e) {
                console.warn('[loadCandidatesData] Query by campaignEmail failed:', e);
            }

            // Combine results
            snapshot = {
                docs: [...emailDocs, ...campaignEmailDocs]
            };
        }

        // Store in cache
        let candidatesArray = [];
        snapshot.docs.forEach(doc => {
            candidatesArray.push({
                id: doc.id,
                ...doc.data()
            });
        });

        // Apply global filter (constituency)
        const globalFilter = window.globalFilterState || {
            constituency: null,
            island: null
        };
        if (globalFilter.initialized && (globalFilter.constituency || globalFilter.island)) {
            const beforeFilterCount = candidatesArray.length;
            candidatesArray = candidatesArray.filter(candidate => {
                // Island takes priority - if island selected, filter by island only
                if (globalFilter.island) {
                    return candidate.island === globalFilter.island;
                } else if (globalFilter.constituency) {
                    // If constituency selected but no island, filter by constituency only
                    return candidate.constituency === globalFilter.constituency;
                }
                return true;
            });
            console.log(`[loadCandidatesData] After global filter (constituency: ${globalFilter.constituency || 'All'}, island: ${globalFilter.island || 'All'}): ${candidatesArray.length} candidates (was ${beforeFilterCount})`);
        }

        dataCache.candidates.data = candidatesArray;
        dataCache.candidates.timestamp = Date.now();
        dataCache.candidates.userEmail = window.userEmail;

        if (candidatesArray.length === 0) {
            tbody.innerHTML = '<tr><td colspan="6" style="text-align: center; padding: 40px; color: var(--text-light);">No candidates registered yet</td></tr>';
            renderPagination('candidates', 0);
            return;
        }

        const sortedCandidates = sortCandidatesArray(candidatesArray);
        updateCandidatesSortIndicators();

        // Pagination
        const state = paginationState.candidates;
        const startIndex = (state.currentPage - 1) * state.recordsPerPage;
        const endIndex = startIndex + state.recordsPerPage;
        const paginatedCandidates = sortedCandidates.slice(startIndex, endIndex);

        // Use DocumentFragment for efficient DOM updates
        const fragment = document.createDocumentFragment();
        paginatedCandidates.forEach((item, index) => {
            const data = item;
            const initials = data.name ? data.name.split(' ').map(n => n[0]).join('').substring(0, 2).toUpperCase() : 'NA';
            const candidateId = data.candidateId || 'N/A';
            const rowNumber = startIndex + index + 1;

            const row = document.createElement('tr');
            row.className = 'candidate-table-row';
            row.setAttribute('data-candidate-id', item.id);
            row.style.cursor = 'pointer';
            row.onclick = () => viewCandidateDetails(item.id);
            row.innerHTML = `
                <td style="text-align: center; color: var(--text-light); font-weight: 600;">${rowNumber}</td>
                <td>
                    <div class="table-cell-user">
                        <div class="user-avatar">${initials}</div>
                        <div>
                            <strong>${data.name || 'N/A'}</strong>
                            ${candidateId !== 'N/A' ? `<span class="table-meta">ID: ${candidateId}</span>` : ''}
                        </div>
                    </div>
                </td>
                <td>${data.position || 'N/A'}</td>
                <td>${data.constituency || 'N/A'}</td>
                <td>${data.island || 'N/A'}</td>
                <td><span class="status-badge ${data.status === 'active' ? 'status-active' : 'status-pending'}">${data.status || 'Pending'}</span></td>
            `;
            fragment.appendChild(row);
        });

        // Use requestAnimationFrame for smooth rendering
        requestAnimationFrame(() => {
            tbody.textContent = '';
            tbody.appendChild(fragment);
        });

        // Render pagination
        renderPagination('candidates', sortedCandidates.length);

        // Auto-select first candidate on initial load (only if detail panel is not already showing)
        const detailPanel = document.getElementById('candidate-detail-panel');
        if (sortedCandidates.length > 0 && detailPanel && detailPanel.style.display === 'none') {
            // Small delay to ensure DOM is ready
            setTimeout(() => {
                const firstCandidate = sortedCandidates[0];
                if (firstCandidate && firstCandidate.id) {
                    viewCandidateDetails(firstCandidate.id);
                }
            }, 100);
        }
    } catch (error) {
        console.error('Error loading candidates:', error);
        tbody.innerHTML = '<tr><td colspan="6" style="text-align: center; padding: 40px; color: var(--text-light);">Error loading candidates</td></tr>';
        renderPagination('candidates', 0);
    }
}

